mapper.select:
select 

from contractitem
where contractId = #{contractid,jdbcType=VARCHAR}
  and contractItemId = #{contractitemid,jdbcType=VARCHAR}

mapper.delete:
delete from contractitem
where contractId = #{contractid,jdbcType=VARCHAR}
  and contractItemId = #{contractitemid,jdbcType=VARCHAR}

mapper.update:
update contractitem
set goodsId = #{goodsid,jdbcType=VARCHAR},
  goodsName = #{goodsname,jdbcType=VARCHAR},
  clientId = #{clientid,jdbcType=VARCHAR},
  goodsNum = #{goodsnum,jdbcType=INTEGER},
  goodsOutPrice = #{goodsoutprice,jdbcType=DOUBLE},
  itemStatus = #{itemstatus,jdbcType=VARCHAR}
where contractId = #{contractid,jdbcType=VARCHAR}
  and contractItemId = #{contractitemid,jdbcType=VARCHAR}

